package com.javster.leetcode

fun main() {
    println(longestPalindrome("babad"))
    println(longestPalindrome("abcabc"))
    println(longestPalindrome("abba"))
    println(longestPalindrome("dvdf"))
    println(longestPalindrome("abcabcbb"))
    println(
        longestPalindrome(
            ""
        )
    )
    println(
        longestPalindrome("")
    )

    val time = System.currentTimeMillis()
    println(
        longestPalindrome("")
    )
    println("spent ${System.currentTimeMillis() - time}")
}

fun longestPalindrome(s: String): String {
    if (s.isEmpty()) return ""
    if (s.length == 1) return s

    val table = Array(s.length) { Array(s.length) { false } }
    for (i in s.indices) {
        table[i][i] = true
    }

    var largestI = 0
    var largestJ = 0

    for (a in 1 until s.length) {
        for (b in 0 until s.length - a) {
            val i = b
            val j = b + a
            table[i][j] = if (j - i == 1) s[j] == s[i] else table[i + 1][j - 1] && s[j] == s[i]
            if (table[i][j] && j - i > largestJ - largestI) {
                largestI = i
                largestJ = j
            }
        }
    }


    return s.substring(largestI, largestJ + 1)

}